import React, { Component } from 'react';
import Button from './Button';

export default class TodoList extends Component {

  state = {
    task: '',
  }

  addTask = (ev) => {
    this.setState({
      task: ev.target.value
    })
    ev.preventDefault()
    console.log(this.input.value)
  }



  render() {
    return (
      <div>
        <form>
          <input placeholder="task"/>
          <Button
          text={"Enviar"}
          handleClick={this.addTask}

          />
        </form>
      </div>
    );
  }
}
